Ext.define('flaskextjsuploader.view.MyForm', {
    extend: 'Ext.form.Panel',

    height: 104,
    id: 'form',
    width: 400,
    bodyPadding: 10,
    title: 'Flask ExtJS Uploader',

    initComponent: function() {
        var me = this;

        Ext.applyIf(me, {
            items: [
                {
                    xtype: 'filefield',
                    anchor: '100%',
                    id: 'file_selector',
                    fieldLabel: 'Select File'
                },
                {
                    xtype: 'button',
                    margin: 10,
                    text: 'Reset',
                    listeners: {
                        click: {
                            fn: me.reset_onButtonClick,
                            scope: me
                        }
                    }
                },
                {
                    xtype: 'button',
                    margin: 10,
                    text: 'Upload',
                    listeners: {
                        click: {
                            fn: me.upload_onButtonClick,
                            scope: me
                        }
                    }
                }
            ]
        });

        me.callParent(arguments);
    },

    reset_onButtonClick: function(button, e, options) {
        Ext.ComponentManager.get('file_selector').reset();
    },

    upload_onButtonClick: function(button, e, options) {
        //console.log(Ext.ComponentManager.get('form'));

        var form = Ext.ComponentManager.get('form');

        form.submit({
            url: '/upload',
            waitMsg: 'Uploading...',
            success: function(form, action) {
                Ext.Msg.alert('Success', action.result.message);
            },
            failure: function(form, action) {
                Ext.Msg.alert('Failed', action.result ? action.result.message : 'No Response');
            }
        });
    }

});